var extend = require('./util').extend;

function cleaner(url, options) {
  var split, path, query;

  extend({ early: false }, options);

  if (url.indexOf('?') > -1) {
    split = url.split('?');
    path  = split[0];
    query = split[1];
  } else {
    path  = url;
  }

}

cleaner.RE_MS = /\/{2,}/;

module.exports = cleaner;
